mapper.select:
SELECT USER_ID AS userId, EMAIL, NICKNAME, CUSTOMER_KEY AS customerKey
FROM MEMBER
WHERE USER_ID = #{userId}

mapper.update:
UPDATE MEMBER
SET CUSTOMER_KEY = #{customerKey}
WHERE USER_ID = #{userId}

